create database if not exists dictionary CHARACTER SET utf8 COLLATE utf8_general_ci;

use dictionary;

create table if not exists felhasznalo(
	felhasznalonev varchar(30) not null,
	jelszo varchar(255) not null,
	email varchar(60) not null unique,
	admin boolean not null,
	primary key ( felhasznalonev )
) engine=innodb;

create table if not exists teszt(
	teszt_id int not null auto_increment,
	felhasznalonev varchar(30) not null,
	kitoltes_datuma datetime not null default current_timestamp,
	forrasnyelv varchar(60) not null,
	celnyelv varchar(60) not null,
	primary key ( teszt_id ),
	constraint felhasznalonev_foreign_key
	foreign key ( felhasznalonev ) references felhasznalo ( felhasznalonev )
	on update cascade
	on delete cascade
) engine=innodb;

create table if not exists magyar(
	szo_id int not null auto_increment,
	szo varchar(100) unique,
	primary key ( szo_id )
) engine=innodb;

create table if not exists angol(
	szo_id int not null auto_increment,
	szo varchar(100) unique,
	primary key ( szo_id )
) engine=innodb;

create table if not exists jelentes(
	jelentes_id int not null auto_increment,
	angol_id int not null,
	magyar_id int not null,
	temakor varchar(100),
	szofaj varchar(50),
	primary key ( jelentes_id ),
	constraint angol_foreign_key
	foreign key ( angol_id ) references angol ( szo_id )
	on delete cascade
	on update cascade,
	constraint magyar_foreign_key
	foreign key ( magyar_id ) references magyar ( szo_id )
	on delete cascade
	on update cascade
) engine=innodb;

create table if not exists teszt_szavai(
	jelentes_id int not null,
	teszt_id int not null,
	sikeres_valasz boolean not null,
	constraint jelentes_id_foreign_key
	foreign key ( jelentes_id ) references jelentes ( jelentes_id )
	on delete cascade
	on update cascade,
	constraint teszt_id_foreign_key
	foreign key ( teszt_id ) references teszt ( teszt_id )
	on delete cascade
	on update cascade	
) engine=innodb;

